(function(){"use strict";var shareButtons=document.querySelector(".js-lf-buttons");if(shareButtons&&lightning_fast_buttons_settings.api_key){querySharedCount()}function querySharedCount(){var request=new XMLHttpRequest;var url=encodeURIComponent(location.href);request.open("GET","//free.sharedcount.com/?apikey="+lightning_fast_buttons_settings.api_key+"&url="+url,true);request.onload=function(){if(this.status>=200&&this.status<400){var data=JSON.parse(this.response);displayCount(".js-facebook-count",data.Facebook.total_count);displayCount(".js-linkedin-count",data.LinkedIn);displayCount(".js-google-count",data.GooglePlusOne);displayCount(".js-pinterest-count",data.Pinterest);displayCount(".js-stumbleupon-count",data.StumbleUpon)}};request.send()}function displayCount(selector,count){var element=document.querySelector(selector);if(element){if(count&&count>0){element.innerHTML=count;element.classList.add("is-visible")}}}})();